import React from 'react';
import {FadeIn} from 'react-slide-fade-in';
import "../styles/news.css";
import grad_pic from '../media/grad_pic.png';
import asf_pic from '../media/asf_pic.jpeg';
import freeskate_pic from '../media/freeskate_pic.jpg';
import cra_pic from '../media/cra_pic.jpg';

const NewsItem = ({pic, date, text}) => {
  return (
    <div class="news-item-box">
      <img class="news-item-image" src={pic} />
      <p class="news-item-date">{date}</p>
      <p class="news-item-text">{text}</p>
    </div>
  )
};

const News = () => {
  return (
    <div id="news-div">
      <FadeIn
      durationInMilliseconds={500}
      >
        <h1>News</h1>
      </FadeIn>
      <FadeIn
      from="bottom"
      positionOffset={50}
      durationInMilliseconds={500}
      >
      <div id="news-items-div">
        <NewsItem 
          pic={grad_pic}
          date="May 2023"
          text="Graduated with high distinction and departmental honors, earning the Class of '94 Award, the CS dept's top award."
        />
        <NewsItem 
          pic={asf_pic}
          date="March 2023"
          text="Test caption."
        />
        <NewsItem
          pic={cra_pic}
          date="March 2022"
          text="Test caption."
        />
        <NewsItem 
          pic={freeskate_pic}
          date="February 2022"
          text="Test caption."
        />
      </div>
      </FadeIn>
    </div>
  )
};

export default News;
