import React from 'react';
import {FadeIn} from 'react-slide-fade-in';
import "../styles/news.css";
import grad_pic from '../media/grad_pic.png';
import asf_pic from '../media/asf_pic.jpeg';
import freeskate_pic from '../media/freeskate_pic.jpg';
import cra_pic from '../media/cra_pic.jpg';

const NewsItem = ({pic, date, text}) => {
  return (
    <div className="news-item-box">
      <img className="news-item-image" src={pic} />
      <p className="news-item-date">{date}</p>
      <div className="news-item-text">{text}</div>
    </div>
  )
};

const News = () => {
  return (
    <div id="news-div">
      <FadeIn
      durationInMilliseconds={500}
      >
        <h1 className="section-head">News</h1>
      </FadeIn>
      <FadeIn
      from="bottom"
      positionOffset={50}
      durationInMilliseconds={500}
      >
      <div id="news-items-div">
        <NewsItem 
          pic={grad_pic}
          date="2023"
          text={<p><a href="https://hmc.meritpages.com/stories/Amani-Rune-Maina-Kilaas-Graduates-from-Harvey-Mudd-College/108741743">Graduated from HMC</a> with high distinction and honors, additionally earning the <a href="https://www.hmc.edu/cs/student-awards/">Class of '94 Award</a>, the CS department's top award.</p>}
        />
        <NewsItem 
          pic={asf_pic}
          date="2023"
          text={<p>Awarded the <a href="https://www.hmc.edu/about/2022/08/12/harvey-mudd-college-2022-astronaut-scholars/">Astronaut Scholarship</a> by NASA Astronaut and HMC alumnus, <a href="https://www.hmc.edu/calendar/events/astronaut-scholarship-foundation-celebration-with-astronaut-george-pinky-nelson-72/">Pinky Nelson</a>.</p>}
        />
        <NewsItem
          pic={cra_pic}
          date="2022"
          text={<p>Awarded the <a href="https://cra.org/2022-outstanding-undergraduate-researcher-award-recipients/">2022 CRA Outstanding Undergraduate Researcher Award</a> and interviewed for the <a href="https://magazine.hmc.edu/spring-2022/this-kind-of-fun/">Mudd Magazine</a>.</p>}
        />
        <NewsItem 
          pic={freeskate_pic}
          date="2022"
          text={<p>Interviewed by the <a href="https://themuddraker.org/2022/02/12/skate-society/">Muddraker</a> on reviving the HMC Skate Society and bringing <a href="https://www.youtube.com/watch?v=QW5MUFjWts8">freeskating</a> back to campus.</p>}
        />
      </div>
      </FadeIn>
    </div>
  )
};

export default News;
