import { BrowserRouter as Router, useLocation} from 'react-router-dom';
import { useEffect } from "react";

const ScrollToTop = () => {
  const { pathname } = useLocation();
  useEffect(() => {
    window.scrollTo(0, 0);
  }, [pathname]);
  return null;
}

function App() {
  return (
    <Router>
      <ScrollToTop />
      <Website />
    </Router>
  )
};

export default App;
