import React from 'react';
import {
  Link,
  useParams,
} from "react-router-dom";
import FadeIn from 'react-fade-in';

const toPagename = (page) => {
  switch (page) {
    case "misc":
      return "Misc";
    case "research":
      return "Research";
    case "cv":
      return "CV";
    default:
      return "Whoops!";
  }
}

const Sidebar = () => {
  const {page} = useParams();
  document.title = (page ? toPagename(page) + " — " : "") + "Amani Maina-Kilaas";
  return (
    <div id="menu-div">
       <FadeIn>
        <h1>Amani Maina-Kilaas</h1>
        <Link to="/" className={page === undefined ? "active-page" : ""}>about</Link>
        <Link to="/research" className={page === "research" ? "active-page" : ""}>research</Link>
        <Link to="/cv" className={page === "cv" ? "active-page" : ""}>cv</Link>
        <Link to="/misc" className={page === "misc" ? "active-page" : ""}>misc</Link>
      </FadeIn>
    </div>
  );
}

export default Sidebar;
