import React from 'react';
import {FadeIn} from 'react-slide-fade-in';
import amani_pic from '../media/amani.jpg';
import "../styles/home.css";
import { introduction } from '../content/profile_info.js';

const makeIcon = (icon) => {
  return (
    <a key={icon.link} className={icon.className.includes("ai ai-") ? "icon ai-icon" : "icon"} href={icon.link}>
      <i className={icon.className}></i>
    </a>
  );
}

const icons = [
  {
    className: "ai ai-cv",
    link: process.env.PUBLIC_URL + '/maina-kilaas_cv.pdf'
  },
  {
    className: "ai ai-semantic-scholar",
    link: "https://www.semanticscholar.org/author/Amani-Rune-Maina-Kilaas/2051872161"
  },
  {
    className: "fa-brands fa-google",
    link: "https://scholar.google.com/citations?user=46I8yggAAAAJ"
  },
  {
    className: "fab fa-linkedin-in",
    link: "https://www.linkedin.com/in/amanirmk/"
  },
  {
    className: "fa-brands fa-twitter",
    link: "https://twitter.com/amanirmk",
  },
  {
    className: "fas fa-paper-plane",
    link: "mailto:amanirmk@mit.edu"
  }
];

const Home = () => {
  return (
    <div id="home-div">
      <FadeIn
      durationInMilliseconds={500}
      >
        <h1 className="section-head">About</h1>
      </FadeIn>
      <FadeIn
      from="bottom"
      positionOffset={50}
      durationInMilliseconds={500}
      >
      <div id="intro-div">
        <div id="text-and-icons">
          {introduction}
          <div id="icons">
            <link rel="stylesheet" href="https://cdn.jsdelivr.net/gh/jpswalsh/academicons@1/css/academicons.min.css"></link>
            {icons.map(makeIcon)}
          </div>
        </div>
        <img id="profile-pic" src={amani_pic}/>
      </div>
      </FadeIn>
      </div>
  );
}

export default Home;
