import React from 'react';
import {FadeIn} from 'react-slide-fade-in';
import "../styles/news.css";
import grad_pic from '../media/grad_pic.png';
import asf_pic from '../media/asf_pic.jpeg';
import freeskate_pic from '../media/freeskate_pic.jpg';
import cra_pic from '../media/cra_pic.jpg';

const NewsItem = ({pic, date, text}) => {
  return (
    <div class="news-item-box">
      <img class="news-item-image" src={pic} />
      <p class="news-item-date">{date}</p>
      <div class="news-item-text">{text}</div>
    </div>
  )
};

const News = () => {
  return (
    <div id="news-div">
      <FadeIn
      durationInMilliseconds={500}
      >
        <h1>News</h1>
      </FadeIn>
      <FadeIn
      from="bottom"
      positionOffset={50}
      durationInMilliseconds={500}
      >
      <div id="news-items-div">
        <NewsItem 
          pic={grad_pic}
          date="May 2023"
          text={<p>Graduated from HMC with high distinction and honors, additionally earning the Class of '94 Award, the <a href="https://www.hmc.edu/cs/student-awards/">CS deptartment's top award</a>.</p>}
        />
        <NewsItem 
          pic={asf_pic}
          date="March 2023"
          text={<p>Awarded the <a href="https://www.hmc.edu/about/2022/08/12/harvey-mudd-college-2022-astronaut-scholars/">Astronaut Scholarship</a> by NASA Astronaut and HMC alumnus, <a href="https://www.hmc.edu/calendar/events/astronaut-scholarship-foundation-celebration-with-astronaut-george-pinky-nelson-72/">Pinky Nelson</a>.</p>}
        />
        <NewsItem
          pic={cra_pic}
          date="March 2022"
          text={<p>Awarded the 2022 Outstanding Undergraduate Researcher Award by the Computing Research Association and interviewed for the HMC magazine</p>}
        />
        <NewsItem 
          pic={freeskate_pic}
          date="February 2022"
          text="Test caption."
        />
      </div>
      </FadeIn>
    </div>
  )
};

export default News;
