import React from 'react';
import {FadeIn} from 'react-slide-fade-in';
import "../styles/news.css";
import grad_pic from '../media/grad_pic.jpg';
import asf_pic from '../media/asf_pic.jpeg';
import freeskate_pic from '../media/freeskate_pic.jpg';
import cra_pic from '../media/cra_pic.jpg';
import { news_items } from '../content/news_items.js';

const NewsItem = ({pic, year, text_p}) => {
  return (
    <div className="news-item-box">
      <img className="news-item-image" src={pic} />
      <p className="news-item-date">{year}</p>
      <div className="news-item-text">{text_p}</div>
    </div>
  )
};

const makeNewsItem = (news_item) => 
<NewsItem
   pic={news_item.pic}
   year={news_item.year}
   text_p={news_item.text_p}
/>;

const News = () => {
  return (
    <div id="news-div">
      <FadeIn
      durationInMilliseconds={500}
      >
        <h1 className="section-head">News</h1>
      </FadeIn>
      <FadeIn
      from="bottom"
      positionOffset={50}
      durationInMilliseconds={500}
      >
      <div id="news-items-div">
        {news_items.map(makeNewsItem)}
      </div>
      </FadeIn>
    </div>
  )
};

export default News;
