import {
  Link,
  useParams,
} from "react-router-dom";

const toPagename = (page) => {
  switch (page) {
    case "research":
      return "Research";
    case "news":
      return "News";
    default:
      return "Whoops!";
  };
}

const Sidebar = () => {
  const {page} = useParams();
